function [ img ] = enlarge(img, zoom, method)% enlarge - magnify the central portion of an image to fill same array size%% syntax: Y=enlarge(X,m,method); % size(Y)=size(X)%	% input: %	X=matrix containing the image to be enlarged, %	m= magnification factor for enlargement%	method = 'nearest'  nearest neighbor interpolation%		'bilinear' bilinear interpolation, %       or 'bicubic'  bicubic interpolation (default) %% output: Y = enlarged image.  size(Y) = size(X)%% LNT 14Mar04%    if nargin<3 || isempty(method), method = 'bicubic'; end    [r, c] = size(img);     r = round(r/2);    c = round(c/2);    x = [r-round(r/zoom)+1:r+round(r/zoom)];	% range of rows    y = [c-round(c/zoom)+1:c+round(c/zoom)];	% range of cols    crop = img(x, y);                           % extract the central core    img = imresize(crop, zoom, method);         % enlarge center to its original size 