% myopicEye.m - script to evaluate stretching for Toco% 8 Feb 03 LNTt = [0:1:15]*pi/180;		% vector of polar anglesr = 23.57/2;				% axial radius of emmetropic eyex=r*cos(t); y=r*sin(t);		% points on the circles = lint(x,y);				% cumulative arc lengths of a circlea = 31.4/2;					% semi major axisb = r;						% semi minor axisp = atan(tan(t)*a/b);		% parameterize the ellipsexx=a*cos(p); yy=b*sin(p);	% points on the ellipsess = lint(xx,yy);			% cumulative arc lengths of an ellipsestretch = ss./s;  stretch(end)	% ans =~ a/r = 1.332 = sol'n if b=afigure;circle(r,0,0,'r');ellipse(a,b,0,0,0,'r');line (x,y);hold on; axis equalline(xx,yy);