function test()res = 512;    figure(40);             [x,y] = meshgrid(linspace(-1, 1, res));    r = (x.*x+y.*y)/2;                             z = exp(-8*pi*r.*r);    z = z/max(z(:));    z = z.^4;        subplot(2,1,1);        z = z.^4;        plot(z(:,floor(res/2)));        res = 1024;            [x,y] = meshgrid(linspace(-1, 1, res));    r = (x.*x+y.*y)/2;                             z = exp(-8*pi*r.*r.*r.*r);    z = z/max(z(:));            subplot(2,1,2);        z = z.^4;        plot(z(:,floor(res/2)));end